package one;

public class day4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//Relation operattors
		//<,>,<=,>=,!=,==
		//! negate
		System.out.println(4>3);
		//boolean value (true or false)
		System.out.println(5<9);
		System.out.println(5<=9);
		System.out.println(5>=9);
		System.out.println(5!=9);
		System.out.println(5==9);
		
		
		//logical operation
		//&&(AND)
		//true&&true===>true
		//true&&false===>false
		//false&&true===>false
		//false&&false===>false
     System.out.println(3 == 3 && 3 > 1);
     System.out.println(3 >= 3 && 5 == 7);
     System.out.println(3 <= 2 && 5 == 5);
     System.out.println(3 != 3 && 4 < 3);
     
     //OR||
        //true&&true===>true
   		//true&&false===>true
   		//false&&true===>true
   		//false&&false===>false
     
     System.out.println(3 == 3 || 3 > 2);
     System.out.println(3 >= 3 || 5 == 7);
     System.out.println(3 <= 2 || 5 == 5);
     System.out.println(3 != 3 || 4 < 3);
     
     //NOT !
     
     System.out.println(!(4==4));
     System.out.println(!(7>=8));
     
     
     
     
     
     
     
     
     
 		
	}

}
