package one;

public class day8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// for while
		System.out.println(1);
		System.out.println(2);
		System.out.println(3);
		System.out.println(4);
		System.out.println(5);

		// to print from 1 to 1000

		// for loop

//		fot (intitilization; conditioncheck; increment/decrement ){

		// }

		for (int i = 0; i <= 5; i++) {
			System.out.println("hello");
		}
		for (int c = 3; c >= 1; c--) {
			System.out.println(c);
			System.out.println("hello");
		}

		for (int a = 1; a <= 6; a++) {
			System.out.println(a);
		}

		for (int i = 2; i <= 20; i = i + 2) {
			System.out.println(i);
		}

		for (int b = 3; b <= 30; b = b + 3) {
			System.out.println(b);
		}
		
		
		//using break
		for (int a=3; a<=8; a++) {
			System.out.println(a);
			if (a==5) {
					break;}
		}

		for (int s=5; s>=3; s--) {
			if (s==3) {
				break;}
				System.out.println(s);
			
		}
		
		//continue statement with for
		for (int i=1; i<=5; i++) {
			if (i==3) {
				continue;}
			System.out.println(i);
		}
		
		
	
		
		
		
		
		
		
		//for loop 
		//multiplication of 5
		
		for (int w=5; w<=50; w=w+5) {
			System.out.println(w);
			
		}
		
		
		for (int e=7; e>=3; e--) {
			if (e==3) {
				break;}
				System.out.println(e);
		}
		
		
		
		for (int a1=3; a1<=8; a1++) {
			System.out.println(a1);
			if (a1==5) {
					break;}
		
		}
		
		
		
		
		
		
	}

}
